import "./App.css";
import React from "react";

import Grid from "./components/Grid";

import axios from "axios";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount = async () => {
    const content = await axios.get("https://pokeapi.co/api/v2/pokemon");
    this.setState({ content: content.data.results });
  };

  render() {
    console.log(this.state.contentcontent);
    return (
      <div className="App">
        <h1>HTTPS requests</h1>
        <h2>Async await</h2>
        <h2>Promises</h2>
        <h1>Pokemon's index</h1>
        <Grid content={this.state.content} />
      </div>
    );
  }
}

export default App;
