import React from "react";

export default function Popup({ display = true, text, img1, img2 }) {
  let [defDisplay, setDefDisplay] = React.useState(display);

  return (
    <div className={(defDisplay ? "" : "hidden") + " popup"}>
      <button onClick={() => setDefDisplay(!defDisplay)}>Close</button>
      <p>{text}</p>
      <img src={img1} alt="front" />
      <img src={img2} alt="back" />
    </div>
  );
}
