import "./App.css";
import React from "react";
import axios from "axios";
import Grid from "./components/Grid";
import Popup from "./components/Popup";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      content: [{ name: "0" }],
      popup: false,
      popupText: "",
    };
  }
  componentDidMount() {
    const onMount = async () => {
      await axios
        .get("https://pokeapi.co/api/v2/pokemon?limit=1000")
        .then((res) => {
          const resp = res.data.results;
          this.setState({ content: resp });
        });
    };
    onMount();
  }
  clickHandler = (t) => {
    const dup = this.state;
    dup.popup = true;
    this.setState(dup);
  };
  render() {
    return (
      <div className="App">
        <h1>HTTPS requests</h1>
        <h2>Async await</h2>
        <h2>Promises</h2>
        <h1>Pokemon's index</h1>
        <Grid content={this.state.content} clickHandler={this.clickHandler} />
        <Popup
          text="hello der"
          display={this.state.popup}
          clickHandler={() => {
            const dup = this.state;
            dup.popup = false;
            this.setState(dup);
          }}
        />
      </div>
    );
  }
}

export default App;
